import React from 'react'
import styled from '@emotion/styled'

const Container = styled.div`
    display: flex;
    flex-flow: column wrap;
    border: 1px solid black;
    padding: 20px;
    align-items: center;
    &:hover {
        display: none;
    }
`

const Emoji = styled.div`
    font-size: 50px;
`

const Card = ({ emoji }) => {
    return (
        <Container>
            <Emoji>{emoji}</Emoji>
            <button>Click to copy</button>
        </Container>
    );
}

export default Card;
