import React, { useEffect, useState} from 'react'
import styled from "@emotion/styled";
import Clipboard from "clipboard";


const Card = styled.div`
    display: flex;
    cursor: pointer;
    flex-direction: column;
    margin: 5px;
    -webkit-box-shadow: 9px 7px 108px -28px rgba(0,0,0,0.75);
-moz-box-shadow: 9px 7px 108px -28px rgba(0,0,0,0.75);
box-shadow: 9px 7px 108px -28px rgba(0,0,0,0.75);
`

const EmojiWrapper = styled.div`
    font-size: 35px;
    padding: 15px;
    flex-grow = 1;
`

const Label = styled.div`
    font-size: 10px;
    flex-grow: 1;
    align-self: flex-start;
    background-color: lightcyan;
    padding: 5px;
    border-radius: 5px;
    margin-bottom: 5px;
    color: darkblue;
    opacity: ${props => props.visible? '1' : '0'};
    flex-grow = 0;
    transition: opacity 0.3s ease;
`

const Emoji = ({ emoji }) => {

    const [visible, setVisible] = useState(false);

    useEffect(() => {
        const clipboard = new Clipboard(".emoji");
        return () => {
            clipboard.destroy();
        };
    })

    const handleClick = (e) => {
        setVisible(true);
        setTimeout( () => setVisible(false), 900);
    }

    return (
        <Card onClick={handleClick} className="emoji" data-clipboard-text={emoji} >
            <EmojiWrapper>{emoji}</EmojiWrapper>
            <Label visible={visible}>Copied! </Label>
        </Card>
    );
}

export default Emoji;
