import React, { useState } from 'react';
import Grid from './components/Grid'
import styled from '@emotion/styled'

const isEmoji = require('is-emoji-keyword');
const emojis = require('emojis-list')

const Header = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-bottom: 30px;
`
const Input = styled.input`
    width: 60%;
    font-size: 15px;
    padding: 10px;
`

function App() {

    const [search, setSearch] = useState('');

    const handleChange = (e) => {
        setSearch(e.target.value)
        if (isEmoji(e.target.value))
            console.log('eureka!!')
    }

    return (
      <>
        <Header>
          <h1>Emoji searcher</h1>
                <Input value={search} onSubmit={() => isEmoji(search) } onChange={handleChange} placeholder={'Search for some emojis here '+emojis[420]} />
        </Header>
        <Grid emojis={emojis}/>
    </>
    );
}

export default App;
