import React from 'react'
import styled from '@emotion/styled'

const Container = styled.div`
    display: flex;
    flex-flow: column wrap;
    border: 1px solid black;
    padding: 35px;
    align-items: center;
    :hover > .Button {
        color: grey;
    }
`

const Emoji = styled.div`
    font-size: 50px;
`

const Button = styled.div`
    font-size: 15px;
    color: white;
    align-self: flex-end;
    border: 1px solid black;
    flex: 1 0 auto;
`


const Card = ({ emoji }) => {

    const handleClick = (e) => {
        document.execCommand("copy");
    }

    return (
        <Container>
            <Emoji>{emoji}</Emoji>
            <Button onClick={handleClick} className={'Button'}>Click to copy</Button>
        </Container>
    );
}

export default Card;
