import React, { useEffect, useState} from 'react'
import styled from "@emotion/styled";
import Clipboard from "clipboard";


const Card = styled.div`
    display: flex;
    cursor: pointer;
    border-radius: 15px;
    flex-direction: column;
    margin: 5px;
    width: 150px;
    height: 150px;
    -webkit-box-shadow: 9px 7px 85px -21px rgba(133,131,133,1);
    -moz-box-shadow: 9px 7px 85px -21px rgba(133,131,133,1);
    box-shadow: 9px 7px 85px -21px rgba(133,131,133,1);
    background-color: white;
    position: relative;
`

const EmojiWrapper = styled.div`
    font-size: 45px;
    flex-grow: 1;
    display: flex;
    justify-content: center;
    align-items: center;
`

const Label = styled.div`
    position: absolute;
    font-size: 10px;
    bottom: 0;
    border: 1px solid black;
    background-color: lightcyan;
    padding: 5px;
    border-radius: 5px;
    margin: 5px;
    color: darkblue;
    opacity: ${props => props.visible? '1' : '0'};
    flex-grow = 0;
    transition: opacity 0.3s ease;
`

const Emoji = ({ emoji }) => {

    const [visible, setVisible] = useState(false);

    useEffect(() => {
        const clipboard = new Clipboard(".emoji");
        return () => {
            clipboard.destroy();
        };
    })

    const handleClick = (e) => {
        setVisible(true);
        setTimeout( () => setVisible(false), 900);
    }

    return (
        <Card onClick={handleClick} className="emoji" data-clipboard-text={emoji} >
            <EmojiWrapper>{emoji}</EmojiWrapper>
            <Label visible={visible}>Copied! </Label>
        </Card>
    );
}

export default Emoji;
