import React from 'react'
import styled from "@emotion/styled";

const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    font-size: 50px;
    color: #64B6AC;
    text-shadow: 1px 1px #5D737E;
    > p {
        color: black;
        font-size: 15px;
        text-shadow: none;
    }
    > h2 {
    border: 1px solid black;
    }
`

const Header = () => {
    return (
        <>
            <Container>
                <h1>Emoji Picker 🍒</h1>
            <p>Based in https://braydentw.github.io/react-emoji-search/</p>
            </Container>
        </>
    );
}

export default Header;
