import React from "react";
import styled from '@emotion/styled'

const Container = styled.div`
    display: flex;
    flex-wrap: nowrap;
    justify-content: center;

`

const Grid = ({ emojis }) => {
    let id = 0;

    const list = emojis.map( emoji => {
        return <Card emoji={emoji} key={id++}/>
    })

    return (
        <Container>
            {list}
        </Container>
    )
}

export default Grid;
